import React from 'react';

function VoteTeam({ team, index, voteHomeTeam, voteDraw, voteAwayTeam }) {
    return (
      <div
        className="team"
        style={{ textDecoration: team.isCompleted ? "line-through" : "" }}
      >
        {team.homeName}

        <div>
          <button onClick={() => voteHomeTeam(index)}>H</button>
          <button onClick={() => voteDraw(index)}>D</button>
          <button onClick={() => voteAwayTeam(index)}>A</button>
        </div>
      </div>


    );
  }

  export default VoteTeam;
