import React from 'react';
import logo from '../img/1.jpg';

function VoteTeam({ team, index, voteHomeTeam, voteDraw, voteAwayTeam }) {
    return (
        <div className="card" style={{ display: index===0 ? "" : "none" }}>
            <div className="row">
                <div className="column">
                    <h3>{team.homeName}</h3>
                    <img className="team-logo" src={logo} alt="Logo" />
                    <p className="title">{index}</p>
                    <p><button onClick={() => voteHomeTeam(index)}>Home Team</button></p>
                </div>

                <div className="column">
                    <h5>Category: {team.sport}</h5>
                    <h1>VS</h1>
                    <p>
                        Country: {team.country}
                        <br></br>
                        Group: {team.group}
                    </p>
                    <p><button onClick={() => voteDraw(index)}>Draw</button></p>
                </div>
                <div className="column">
                    <h3>{team.awayName}</h3>
                    <img className="team-logo" src={logo} alt="Logo" />
                    <p className="title">CEO & Founder, Example</p>
                    <p><button onClick={() => voteAwayTeam(index)}>Away Team</button></p>
                </div>
            </div>   
        </div>  
    );
  }

  export default VoteTeam;
