import React, { useState, useEffect } from 'react';
import MyPoll from "./MyPoll";

function Polls() {
    const [votedTeams, setvotedTeams] = useState([]);

    useEffect(() => {
        if(!window.localStorage.getItem("votedTeams")) {
          setvotedTeams([votedTeams, JSON.stringify(window.localStorage.getItem("votedTeams"))]);
        }
    }, [votedTeams]);

    return (
        <div>

        <div style={{ display: votedTeams.length===0 ? "" : "none", textAlign: "center", color: "#ffffff"}}>
            <p>Aha! no polls here.</p>
        </div>
    </div>
    );
}

export default Polls
