import React, { useState, useEffect } from 'react';
import data_file from "../db/test-assignment";
import VoteTeam from "./VoteTeam";

function Teams() {
  const [teams, setTeams] = useState([]);  

  const voteHomeTeam = index => {
    const newTodos = [...teams];
    newTodos[index].isCompleted = true;
    setTeams(newTodos);
  };

  const voteDraw = index => {
    const newTodos = [...teams];
    newTodos.splice(index, 1);
    setTeams(newTodos);
  };

  const voteAwayTeam = index => {
    const newTodos = [...teams];
    newTodos.splice(index, 1);
    setTeams(newTodos);
  };

  useEffect(() => {
    setTeams(data_file);
  }, []);

  return (
    <div>
        {teams.map((team, index) => (  
            <VoteTeam
                key={index}
                index={index}
                team={team}
                voteHomeTeam={voteHomeTeam}
                voteDraw={voteDraw}
                voteAwayTeam={voteAwayTeam}
            />
        ))}
    </div>
  );
}
export default Teams;
