import React, { useState, useEffect } from 'react';
import MyPoll from "./MyPoll";

function Polls() {
    const [votedTeams, setVotedTeams] = useState([]);

    useEffect(() => {
        setVotedTeams(JSON.parse(window.localStorage.getItem("votedTeams")));
      }, []);
    return (
        <div>
        {votedTeams.map((votedTeam, index) => (  
            <MyPoll
                key={index}
                team={votedTeam}
            />
        ))}
        <div style={{ display: votedTeams.length===0 ? "" : "none", textAlign: "center", color: "#ffffff"}}>
            <p>Aha! no polls here.</p>
        </div>
    </div>
    );
}

export default Polls
